import address from 'address';
import { md5Encoding } from '../coding';

// 获取本机 Mac 地址
export async function getMacAddress(): Promise<string> {
    return new Promise((resolve) => {
        address.mac((err, mac) => {
            if (err) {
                resolve('00:00:00:00:00:00');
                return;
            }
            resolve(mac || '00:00:00:00:00:00');
        });
    });
}
